import React, { useState, useEffect, useContext } from 'react';
import { ShopContext } from '../Context/ShopContext';
import { useParams } from 'react-router-dom';
import Breadcrum from '../Components/Breadcrums/Breadcrum';
import ProductDisplay from '../Components/ProductDisplay/ProductDisplay';
import RelatedProducts from '../Components/RelatedProducts/RelatedProducts';

const Product = () => {
  const [all_products, setAll_Product] = useContext([]);
   all_products  = useContext(ShopContext);
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      if (!all_products || all_products.length === 0) {
        // If all_products is not available, fetch it first
        try {
          const response = await fetch('http://localhost:4000/allproducts');
          const data = await response.json();
          setAll_Product(data);
        } catch (error) {
          console.error('Error fetching all_products:', error);
          return;
        }
      }

      // Now that all_products is available, find the product
      const fetchedProduct = all_products.find((e) => e.id === Number(productId));
      setProduct(fetchedProduct);
      setLoading(false);
    };

    fetchData();
  }, [all_products, productId]);

  // Check if product is defined before rendering
  if (loading) {
    return <div>Loading...</div>; // Display a loading indicator while fetching data
  }

  return (
    <div>
      {product ? (
        <>
          <Breadcrum product={product} />
          <ProductDisplay product={product} />
        </>
      ) : (
        <div>No product found</div>
      )}
      <RelatedProducts />
    </div>
  );
};

export default Product;
