import React, { useState } from 'react'
import './CSS/LoginSignup.css'

const LoginSignup = () => {

  const [state, setState] = useState("Login");
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    email: "",
    address: "",
    contact: "",
  });

  const login = async () => {
    console.log("Login", formData);

    try {
        const response = await fetch('http://localhost:4000/login', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formData),
        });

        const responseData = await response.json();

        if (responseData && responseData.success) {
            localStorage.setItem('auth-token', responseData.token);
            window.location.replace("/");
        } else {
            alert(responseData && responseData.errors ? responseData.errors : "Unknown error");
        }
    } catch (error) {
        console.error('Error during login:', error);
    }
}

const signup = async () => {
    console.log("Signup", formData);

    try {
      // Step 1: Request a verification code
      const verificationResponse = await fetch(
        'http://localhost:4000/sendverificationcode',
        {
          method: 'POST',
          headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ email: formData.email }),
        }
      );

      const verificationData = await verificationResponse.json();

      if (verificationData && verificationData.success) {
        // Step 2: Prompt user for the verification code
        const userVerificationCode = prompt(
          'Enter the verification code sent to your email:'
        );

        // Step 3: Submit the verification code along with other signup data
        const response = await fetch('http://localhost:4000/signup', {
          method: 'POST',
          headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            ...formData,
            verificationCode: userVerificationCode,
          }),
        });

        const responseData = await response.json();

        if (responseData && responseData.success) {
          // Step 4: If signup is successful, log in the user
          const loginResponse = await fetch('http://localhost:4000/login', {
            method: 'POST',
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email: formData.email, password: formData.password }),
          });

          const loginData = await loginResponse.json();

          if (loginData && loginData.success) {
            localStorage.setItem('auth-token', loginData.token);
            window.location.replace('/');
          } else {
            alert(
              loginData && loginData.errors
                ? loginData.errors
                : 'Unknown error during login'
            );
          }
        } else {
          alert(
            responseData && responseData.errors
              ? responseData.errors
              : 'Unknown error during signup'
          );
        }
      } else {
        alert(
          verificationData && verificationData.errors
            ? verificationData.errors
            : 'Error sending verification code'
        );
      }
    } catch (error) {
      console.error('Error during signup:', error);
    }
  };



  const changeHandler = (e) => {
    setFormData({
      ...formData,[e.target.name]:e.target.value
    })
  }

  return (
    <div className='loginsignup'>
      <div className="loginsignup-container">
        <h1>{state}</h1>
        <div className="loginsignup-fields">
          {state === "Sign Up"?<input name='username' value={formData.username} onChange={changeHandler} type="text" placeholder='Your Name' />:<></>}
          <input name='email' value={formData.email} onChange={changeHandler} type="email" placeholder='Email Address' />
          <input name='password' value={formData.password} onChange={changeHandler} type="password" placeholder='Password' />
          {state === "Sign Up" ? <input name='contact' value={formData.contact} onChange={changeHandler} type="text" placeholder='Contact' /> : <></>}
        {state === "Sign Up" ? <input name='address' value={formData.address} onChange={changeHandler} type="text" placeholder='Address' /> : <></>}
        </div>
        <button onClick={()=>{state==="Login"?login():signup()}}>Continue</button>
        {state === "Sign Up" ? 
        <p className='loginsignup-login'>Already have an account? <span onClick={()=>{setState("Login")}}>Login Here</span></p> :
        <p className='loginsignup-login'>Create an account? <span onClick={()=>{setState("Sign Up")}}>Click Here</span></p>}
        <div className="loginsignup-agree">
          <input type="checkbox" name='' id='' />
          <p>By continuing, I agree to the terms of use & privacy policy.</p>
        </div>
      </div>
    </div>
  )
}

export default LoginSignup
