import React from 'react';
import './Hero.css';
import arrow_icon from '../Assets/arrow.png';
import hero_image from '../Assets/hero_image.png';
import { Link } from 'react-router-dom';

const Hero = () => {
  const handleScrollToNewCollections = () => {
    const newCollectionsSection = document.getElementById('newcollections');
    
    if (newCollectionsSection) {
      newCollectionsSection.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className='hero'>
      <div className="hero-left">
        <div>
          <p>Quality Living Starts Here â€“ New and Refurbished Treasures Await!</p>
        </div>
        <div className="hero-latest-button" onClick={handleScrollToNewCollections}>
          <div>Latest Collection</div>
          <img src={arrow_icon} alt="" />
        </div>
      </div>
      <div className="hero-right">
        <img src={hero_image} alt="" />
      </div>
    </div>
  );
};

export default Hero;
