import React, { useState, useEffect, useContext } from 'react';
import { ShopContext } from '../Context/ShopContext';
import { useParams } from 'react-router-dom';
import Breadcrum from '../Components/Breadcrums/Breadcrum';
import ProductDisplay from '../Components/ProductDisplay/ProductDisplay';
import RelatedProducts from '../Components/RelatedProducts/RelatedProducts';

const Product = () => {
  const { all_products } = useContext(ShopContext);
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      // Check if all_products is available
      if (all_products) {
        const fetchedProduct = all_products.find((e) => e.id === Number(productId));
        setProduct(fetchedProduct);
        setLoading(false); // Set loading to false once data is fetched
      }
    };

    // Fetch data only if product is not available
    if (!product) {
      fetchData();
    }
  }, [all_products, product, productId]);

  // Check if product is defined before rendering
  if (loading) {
    return <div>Loading...</div>; // or handle loading state in a different way
  }

  return (
    <div>
      <Breadcrum product={product} />
      <ProductDisplay product={product} />
      <RelatedProducts />
    </div>
  );
};

export default Product;
